mapper.select:
SELECT l.*, ts.stock FROM ( SELECT i.id, i.name, i.price, i.img_url imgUrl, i.item_status itemStatus, s.id seckillId, s.start_time startTime, s.end_time endTime, s.price seckillPrice FROM t_seckill s LEFT JOIN t_item i ON s.item_id = i.id
and i.ITEM_STATUS= #{itemStatus,jdbcType=INTEGER}
ORDER BY s.create_time DESC ) l LEFT JOIN t_item_stock ts ON l.id = ts.item_id
